function getTid() {
    let input = result.get("歌单：");
    if (input === undefined || input === '') {
        //java.toast("请输入歌单！");
        return 0;
    }
    if (input.startsWith("data:")) {
        try {
            let decodedData = java.base64Decode(input.match(/base64,([^,]+)/)[1]);
            return Number(JSON.parse(decodedData).tid);
        } catch (e) {
            //java.toast("解析数据失败！");
            return 0;
        }
    }
    if (input.startsWith("http:")) {
        let match = input.match(/playlist\/(\d+)/);
        if (match) {
            return Number(match[1]);
        } else {
            //java.toast("歌单链接格式错误！");
            return 0;
        }
    }
    num = Number(input);
    if (!isNaN(num) && Number.isInteger(num) && num > 0) {
        return num;
    }
    //java.toast("无法解析歌单tid！");
    return 0;
}

function getSongId() {
    let input = result.get("单曲：");
    if (input === undefined || input === '') {
        java.toast("请输入单曲！");
        return 0;
    }
    if (input.startsWith("data:")) {
        try {
            let decodedData = java.base64Decode(input.match(/base64,([^,]+)/)[1]);
            return Number(JSON.parse(decodedData).id);
        } catch (e) {
            java.toast("解析单曲id失败！");
            return 0;
        }
    }
    num = Number(input);
    if (!isNaN(num) && Number.isInteger(num) && num > 0) {
        return num;
    }
    java.toast("无法解析单曲id！");
    return 0;
}


