    if (tid == 0) {
        java.toast("解析歌单tid失败！");
        return 0;
    }
    data = JSON.stringify({
        "comm": {
            "cv": 4747474,
            "ct": 24,
            "format": "json",
            "inCharset": "utf-8",
            "outCharset": "utf-8",
            "notice": 0,
            "platform": "yqq.json",
            "needNewCode": 1,
            "uin": Number(uin),
            "g_tk_new_20200303": 528327340,
            "g_tk": 528327340
        },
        "req_1": {
            "module": "music.musicasset.PlaylistBaseRead",
            "method": "GetPlaylistByUin",
            "param": {
                "uin": String(uin),
                "bWithoutStatus": false
            }
        }
    });
    v_playlist = zzcSignPost(data).req_1.data.v_playlist;
    for (let item of v_playlist) {
        if (item.tid == tid) {
            //java.toast("匹配id=" + item.dirId);
            return item.dirId;
        }
    }
    java.toast("未创建该歌单！");
