$ = (x) => `{{Url("源站：") + Path}}/search?${x}&orderType=1&page={{page}}&searchType=1&space=${P}&source=${Q}&finished=${Finished}&orderType=${R}&person=${Person}&porn=${Porn}&orientation=${Orientation}&period=${Period}&score=${Score}&size=36`;
u = Url("源站：") + Path + '/';
["最新上架", "最近更新", "最多观看", "最多收藏"].map((p, q) => {
