push("【 排行 】", null, 1);
["周榜", "月榜", "年榜", "总榜"].map((p, q) => {
    push(p, `{{Url("源站：") + Path}}/rank?type=${q + 1}`, null);
});
[["分类", "category"],["标签", "includeTags"]].map([a, b] => {
    push(`【 ${a} 】`, '', 1);
    if (/分类/.test(a)) push(`全部`, $(""), null);
    d = /y/.test(b) ? "categories": "tags";
    url = u + 'app/novel/' + d + '?type=0';
    res = java.ajax(url);
    if(res.match(/no-js/)){
        java.webView('',Url("源站："),'');
        res=java.ajax(url);
    }
    c = JSON.parse(res).model;
    for (i = 0; i < c.length; i++) {
        push(
            c[i].name,
            $(b + "=" + encodeURIComponent(c[i].name)),
            null
        );
    }
    if (/分类/.test(a)) push(`贤者`, $(b + "=" + encodeURIComponent('贤者小说')), null);
});

