<js>
result = result.replace(/.+\s(?:$|\n)|\|$/gm, '');
var BUrl = String(java.get('BUrl'));
var Page = /\d+/.test(Map('页数：')) ? Map('页数：') : '5';
var url1 = BUrl.replace(/novel\/intro\?id/, 'api/novel/app/novel/comments?novelId') + '&sortType=' + Get('a') + '&page=1&rows=' + (parseInt(Page) * 5);
var list1 = [];
var headers = JSON.parse(cache.get("uaaheaders"));
var option = {
    "method": "GET",
    "headers": headers
};
var Url = url1 + ',' + JSON.stringify(option);
result1 = java.ajax(Url);
var Count = JSON.parse(result1).totalCount;
var pageList = JSON.parse(result1).data;
list1 = list1.concat(pageList);
var intro2 = '';
var intro3 = '';
var stars = '★★★★★☆☆☆☆☆';
for (var i = 0; i < list1.length; i++) {
    if (!list1[i]) continue;
    var star = Math.ceil(parseInt(list1[i].score) / 2);
    var intro2_1 = '[' + String(list1[i].createTime).replace(/(.*?)T(.*?)\..*/, '$1 $2') + ']                       评分：' + stars.slice(10-(star + 5), 10-star);
    var intro2_2 = '⛄' + '【' +  list1[i].nickName + '】：' + '👍🏻' + list1[i].awesome;
    var intro2_3 = list1[i].content;
    if (list1[i].replyCount !== 0) {
        var url2 = String(url1).replace(/comments.*$/,'subcomments?parentId=') + list1[i].id + '&lave=';
        var headers = JSON.parse(cache.get("uaaheaders"));
        var option1 = {
            "method": "GET",
            "headers": headers
        };
        var Url1s = [];
        var result2s = [];
        var list2 = [];
        var reply = parseInt(list1[i].replyCount);
        var Page_1 = Math.ceil(reply / 5);
        for (var d = 0; d < Page_1; d++) {
            var lave = reply - (d * 5);
            var Url1 = url2 + lave + ',' + JSON.stringify(option1);
            Url1s.push(Url1);
        }
        result2s = java.ajaxAll(Url1s);
        for (e in result2s) {
            result2 = result2s[e].body();
            var pageList = JSON.parse(result2).data;
            list2 = list2.concat(pageList);
        }
        for (j in list2) {
            if (!list2[j].content) continue;
            var intro2_1_1 = '[' + String(list2[j].createTime).replace(/(.*?)T(.*?)\..*/, '$1 $2') + ']';
            var intro2_2_1 = '⛄' + '【' +  list2[j].nickName + '】：' + '👍🏻' + list2[j].awesome;
            var intro2_3_1 = (list2[j].replyNickName !== '' ? ('@' + list2[j].replyNickName + '：') : '') + list2[j].content;
            intro3 = intro3 +'\nㅤ     ' +  intro2_1_1 + '\nㅤ     ' +  intro2_2_1 +  '\nㅤ     ' +  intro2_3_1;
        }
    }
    intro2 = intro2 + '\nㅤ\n' +  intro2_1 + '\n' +  intro2_2 +  '\n' +  intro2_3 + intro3;
    intro3 = '';
}
var Num = (parseInt(Page) * 5) < Count ? (parseInt(Page) * 5) : Count;
var Review = parseInt(Page) > 0 ? ('☕【评论】' + ['','最新','最热'][Get('a')] + '                                 评论数：' + Num + '/' + Count + intro2) : '☕【评论】未启用';
result = result + '\nㅤ\n' + Review;
result
</js>

