    var params = encryption(encryption(JSON.stringify(param), '0CoJUm6Qyw8W8jud'), i_data);
    var encSecKey = encSecKeyEncryption(i_data, encSecKeyParameterOne, encSecKeyParameterTwo);
    var body = `params=${params}&encSecKey=${encSecKey}`;
    var headers = {
        'content-type': 'application/x-www-form-urlencoded',
        'referer': 'https://music.163.com/'
    };
    var option = {
        "method": "POST",
        "body": body,
        "headers": headers
    };
    var postUrl = url + "," + JSON.stringify(option);
    var resp = JSON.parse(java.ajax(postUrl));
    return resp;
}

function formatDuration(milliseconds) {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
