				'name': songList[i].name,
				'author': "云音乐官方",
				'kind': ["个性推荐"],
				'intro': songList[i].name,
				'cover': songList[i].picUrl,
				'url': `/playlist?id=${songList[i].id}`
			});
		}
	}
} else if (baseUrl.includes("songs/rank")) {

var userInfo = JSON.parse(java.get("userinfo"));
var nickname = userInfo.nickname;
var userId = userInfo.userId;

	// 听歌排行
	songlist.push({
		'name': "听歌排行",
		'author': nickname,
		'kind': ["听歌排行"],
		'intro': " ｡:.ﾟヽ(｡◕‿◕｡)ﾉﾟ.:｡+ﾟ",
		'cover': userInfo.avatarUrl,
		'url': baseUrl
	});
} else if (match && baseUrl.includes("limit=1")) {
	// 外部歌单
	var playlistUrl = 'https://music.163.com/playlist?id=' + match[1];
	var html = java.ajax(playlistUrl);
	var doc = org.jsoup.Jsoup.parse(html);
	var list = doc.select('#m-playlist');
	var name = list.select('.tit').text();
	var author = list.select('span.name a').text();
	var kind = list.select('strong.s-fc6').text();
	var cover = String(list.select('img.j-img').attr('data-src')).split("?")[0];
	var intro = list.select('p#album-desc-more').text();
	var url = playlistUrl;
	songlist.push({
		'name': name,
		'author': author,
		'kind': [kind, "外部歌单"],
		'intro': intro,
		'cover': cover,
		'url': url
	});
} else {
	java.toast("歌单格式错误或未导入歌单");
}

result = songlist;

